mapper.update:
update oa_sys.travelreimbursement
set status = -1
where uid = #{uid}

update oa_sys.travelreimbursement
set status = #{status},
    count  =#{count},
    nextUid= #{nextUid}
where uid = #{uid}

update oa_sys.travelreimbursement
set status        = 2,
    nextUid= null,
    reject_reason = #{reject_reason}
where uid = #{uid}
