mapper.select:
select realeName
from oa_sys.user
where uid = #{uid}

select username
from oa_sys.user
where username = #{username}
  and userType = #{userType}
limit 1

mapper.update:
update oa_sys.user
set realeName = #{realeName},
    gender=#{gender},
    tel=#{tel},
    email=#{email}
where uid = #{uid}

update oa_sys.user
set status = -1
where uid = #{uid}

update oa_sys.user
set password = #{password}
where uid = #{uid}

update oa_sys.user
set username = #{username}
where uid = #{uid}

update oa_sys.user
set status = #{status}
where uid = #{uid}

mapper.select:
select departmentUid
from oa_sys.user
where uid = #{uid}

mapper.update:
update oa_sys.user
set departmentUid = #{departmentUid}
where uid = #{uid}
