mapper.update:
update oa_sys.filename
set status = -1
where uid = #{uid}

update oa_sys.filename
set RowUid = #{RowUid}
where TableUid = #{TableUid}
  and ReleaseUid = #{ReleaseUid}
  and RowUid is null
order by create_time desc

mapper.select:
select oldFileName
from oa_sys.filename
where FileName = #{FileName}

select uid, oldFileName, FileName
from oa_sys.filename
where TableUid = #{TableUid}
  and RowUid = #{RowUid}
  and status = 0

select create_time
from oa_sys.filename
where FileName = #{FileName}
