mapper.update:
update oa_sys.workreportteacher
set status = -1
where uid = #{uid}

update oa_sys.workreportteacher
set status  = #{status},
    count   = #{count},
    nextUid = #{nextUid}
where uid = #{uid}

update oa_sys.workreportteacher
set status        = 2,
    nextUid       = null,
    reject_reason = #{reject_reason}
where uid = #{uid}
