mapper.delete:
delete
from oa_sys.filename
where TableUid = #{TableUid}
  and RowUid = #{RowUid}

mapper.update:
update oa_sys.filename
set RowUid     = #{RowUid},
    update_time=now()
where TableUid = #{TableUid}
  and ReleaseUid = #{ReleaseUid}
  and RowUid is null

mapper.select:
select uid, oldFileName, FileName
from filename
where ReleaseUid = #{ReleaseUid}
  and TableUid = #{TableUid}
  and RowUid is null

select oldFileName
from filename
where FileName = #{FileName}

select uid, oldFileName, FileName
from filename
where TableUid = #{TableUid}
  and RowUid = #{RowUid}
